div.script:
if(document.getElementById("color_1")){
    color_1 = document.getElementById("color_1").value
    if(document.getElementById("color_1")){
       button_chat_1 = document.getElementById("button_chat_1")
       button_chat_1.style.background=color_1

    }
 }

 if(document.getElementById('button_text_color')){
    color_text = document.getElementById('button_text_color').value
    text_button_color = document.getElementById("button_chat_1")
    text_button_color.style.color = color_text
 }
